import React, { useState } from "react";
import { Link, Outlet } from "react-router-dom";
import { useTheme } from "../context/ThemeContext";
import ThemeSwitch from "./ThemeSwitch";

import Footer from "./Footer";


export default function Layout() {
  const { theme } = useTheme();
  const [isOpen, setIsOpen] = useState(false);
  const toggleMenu = () => setIsOpen(!isOpen);
    const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);


  return (
      <div
      style={{
        backgroundColor: theme === "light" ? "#f8f9fa" : "#121212",
        color: theme === "light" ? "#000" : "#fff",
        minHeight: "100vh",
        transition: "all 0.3s ease",
      }}
    >
      <header
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          padding: "15px 25px",
          backgroundColor: theme === "light" ? "#007bff" : "#333",
          color: "#fff",
          position: "relative",
        }}
      >
        <div style={{ fontWeight: "bold", fontSize: "18px" }}>üåê MyApp</div>

        {/* Desktop nav */}
        {!isMobile && (
          <nav style={{ display: "flex", gap: "15px" }}>
            <Link to="/" style={{ color: "#fff", textDecoration: "none" }}>
              Home
            </Link>
            <Link to="/profile" style={{ color: "#fff", textDecoration: "none" }}>
              Profile
            </Link>
            <Link to="/login" style={{ color: "#fff", textDecoration: "none" }}>
              Login
            </Link>
          </nav>
        )}

        {/* Mobile hamburger button */}
        {isMobile && (
          <button
            onClick={toggleMenu}
            style={{
              background: "transparent",
              border: "none",
              color: "#fff",
              fontSize: "24px",
              cursor: "pointer",
            }}
          >
            {isOpen ? "‚úñ" : "‚ò∞"}
          </button>
        )}

        <ThemeSwitch />
      </header>

      {/* Mobile dropdown menu */}
      {isMobile && isOpen && (
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            gap: "12px",
            backgroundColor: theme === "light" ? "#007bff" : "#333",
            padding: "15px 25px",
          }}
        >
          <Link to="/" onClick={toggleMenu} style={{ color: "#fff", textDecoration: "none" }}>
            Home
          </Link>
          <Link to="/profile" onClick={toggleMenu} style={{ color: "#fff", textDecoration: "none" }}>
            Profile
          </Link>
          <Link to="/login" onClick={toggleMenu} style={{ color: "#fff", textDecoration: "none" }}>
            Login
          </Link>
        </div>
      )}

      <main style={{ padding: "20px" }}>
        <Outlet />
      </main>

      <Footer>

      </Footer>
    </div>
  );
}
